mapper.select:
SELECT SEQKEY "seqkey",SEQDESC "seqdesc" ,TO_CHAR(CREATEDATE,'yyyy-MM-dd hh24:mi:ss') "createdate" FROM SYS_SEQ_MAIN
AND SEQKEY=#{seqkey}
ORDER BY CREATEDATE DESC

SELECT SEQKEY "seqkey"
,SEQOWNER "seqowner"
,SEQVALUE "seqvalue"
,TO_CHAR(CREATEDATE,'yyyy-MM-dd hh24:mi:ss') "createdate" FROM SYS_SEQ_VALUE
where SEQKEY=#{seqkey}

    AND SEQOWNER=#{seqowner}

SELECT SEQID "seqid"
,SEQTYPE "seqtype"
,SEQORDER "seqorder"
,SEQTYPEVALUE "seqtypevalue"
,SEQKEY "seqkey"
FROM SYS_SEQ_SETTING


        AND SEQTYPE=#{seqtype}


        AND SEQORDER=#{seqorder}


        AND SEQTYPEVALUE=#{seqtypevalue}


        AND SEQKEY=#{seqkey}


        AND SEQID=#{seqid}


ORDER BY SEQORDER ASC

SELECT
A.SEQKEY "seqkey"
,A.SEQDESC "seqdesc"
,TO_CHAR(A.CREATEDATE,'yyyy-MM-dd hh24:mi:ss') "createdate"
,(SELECT B.SEQVALUE FROM SYS_SEQ_VALUE B
WHERE B.SEQKEY=A.SEQKEY

    AND B.SEQOWNER=#{seqowner}
) "seqvalue" FROM SYS_SEQ_MAIN A
WHERE A.SEQKEY=#{seqkey}

mapper.update:
UPDATE SYS_SEQ_VALUE A SET A.SEQVALUE=A.SEQVALUE+1
WHERE A.SEQKEY=#{seqkey}

    AND A.SEQOWNER=#{seqowner}


    AND A.SEQVALUE=#{seqnum}
