mapper.select:
SELECT SEQID "seqid", SEQKEY "seqkey", SEQDESC "seqdesc", CREATEUSER "createuser", date_format(CREATEDATE,'%Y-%m-%d %H:%i:%s') "createdate" FROM SYS_SEQ_MAIN
AND SEQID=#{seqid}
AND SEQKEY=#{seqkey}
ORDER BY CREATEDATE DESC

mapper.update:
UPDATE SYS_SEQ_MAIN
SET

    SEQDESC=#{seqdesc}

WHERE SEQID=#{seqid}

mapper.select:
SELECT
     S.SEQSETINGID         "seqsetingid",
     S.SEQID               "seqid",
     S.SEQTYPE             "seqtype",
     S.SEQORDER            "seqorder",
     S.SEQTYPEVALUE        "seqtypevalue"
FROM SYS_SEQ_SETTING S


        AND S.SEQSETINGID=#{seqsetingid}


        AND S.SEQID=#{seqid}


        AND S.SEQTYPE=#{seqtype}


        AND S.SEQORDER=#{seqorder}


        AND S.SEQTYPEVALUE=#{seqtypevalue}


ORDER BY S.SEQORDER ASC

mapper.update:
UPDATE SYS_SEQ_SETTING
SET
  SEQTYPE=#{seqtype},
  SEQORDER=#{seqorder},
  SEQTYPEVALUE=#{seqtypevalue}
WHERE SEQID=#{seqsetingid}

mapper.select:
SELECT
A.SEQKEY "seqkey",
A.SEQDESC "seqdesc",
date_format(A.CREATEDATE,'%Y-%m-%d') "createdate",
(SELECT B.SEQVALUE FROM SYS_SEQ_VALUE B
WHERE B.SEQKEY=A.SEQKEY

    AND B.SEQOWNER=#{seqowner}
) "seqvalue"
FROM SYS_SEQ_MAIN A
WHERE A.SEQKEY=#{seqkey}

mapper.update:
UPDATE SYS_SEQ_VALUE A
SET
    A.SEQVALUE=A.SEQVALUE+1
WHERE A.SEQKEY=#{seqkey}

    AND A.SEQOWNER=#{seqowner}


    AND A.SEQVALUE=#{seqnum}
