mapper.select:
SELECT SEQKEY "seqkey",SEQDESC "seqdesc" ,date_format(CREATEDATE,'%Y-%m-%d') "createdate" FROM SYS_SEQ_MAIN
AND SEQKEY=#{seqkey}
ORDER BY CREATEDATE DESC

SELECT
    SEQID "seqid",
    SEQTYPE "seqtype",
    SEQORDER "seqorder",
    SEQTYPEVALUE "seqtypevalue",
    SEQKEY "seqkey"
FROM SYS_SEQ_SETTING


        AND SEQTYPE=#{seqtype}


        AND SEQORDER=#{seqorder}


        AND SEQTYPEVALUE=#{seqtypevalue}


        AND SEQKEY=#{seqkey}


        AND SEQID=#{seqid}


ORDER BY SEQORDER ASC

SELECT
A.SEQKEY "seqkey",
A.SEQDESC "seqdesc",
date_format(A.CREATEDATE,'%Y-%m-%d') "createdate",
(SELECT B.SEQVALUE FROM SYS_SEQ_VALUE B
WHERE B.SEQKEY=A.SEQKEY

    AND B.SEQOWNER=#{seqowner}
) "seqvalue"
FROM SYS_SEQ_MAIN A
WHERE A.SEQKEY=#{seqkey}

mapper.update:
UPDATE SYS_SEQ_VALUE A
SET
    A.SEQVALUE=A.SEQVALUE+1
WHERE A.SEQKEY=#{seqkey}

    AND A.SEQOWNER=#{seqowner}


    AND A.SEQVALUE=#{seqnum}
