mapper.select:
select
M.MENUID menuid,
M.MENUNAME menuname,
M.MENUTYPE menutype,
M.MENUORDER menuorder,
M.PARENTID parentid,
M.MENULEVEL menulevel,
M.ACTION action,
M.STATUS status,
M.CREATEDATE createdate,
M.UPDATEDATE updatedate
FROM SYSTEM_RESOURCES M


        AND M.MENUID =#{menuid}


        AND M.MENUNAME =#{menuname}


        AND M.MENUTYPE =#{menutype}


        AND M.MENUORDER =#{menuorder}


        AND M.MENULEVEL =#{menulevel}


        AND M.STATUS =#{status}


        AND M.PARENTID=#{parentid}


        AND M.MENUID=#{supmenuid}


        AND M.ACTION IS NOT NULL



ORDER BY M.MENUORDER ASC

select M.MENUID menuid, M.MENUNAME menuname, M.MENUTYPE menutype, M.MENUORDER menuorder, M.PARENTID parentid, M.MENULEVEL menulevel, M.STATUS status, (CASE WHEN menulevel='1' THEN 'true' WHEN menulevel='2' THEN 'true' END) open FROM SYSTEM_RESOURCES M
AND M.MENUID =#{menuid}
ORDER BY M.MENUORDER ASC

mapper.update:
UPDATE  SYSTEM_RESOURCES A
SET
A.MENUORDER=#{menuorder}
WHERE

    A.MENUID=#{menuid}


    A.MENUID='-1'
